<?php    namespace Wptt;    \CModule::IncludeModule('iblock');    \CModule::IncludeModule('highloadblock');    class Main {        /**         * Дебаг массивов         * @param array $array         */        static public function debug($array = []) {            echo '<pre>';            var_dump($array);            echo '</pre>';        }        static public function num2word($n, $words)        {            return ($words[($n = ($n = $n % 100) > 19 ? ($n % 10) : $n) == 1 ? 0 : (($n > 1 && $n <= 4) ? 1 : 2)]);        }        static public function isFirstVowels($str): bool        {            $result = false;            $firstLetter = mb_substr(mb_strtolower($str), 0, 1);            $vowels = ['а', 'у', 'о', 'ы', 'и', 'э', 'я', 'ю', 'ё', 'е'];            if (array_search($firstLetter, $vowels)) {                $result = true;            }            return $result;        }    }