<?php foreach ($arResult['ITEMS'] as $key => $arItem) {    $arSelect = Array("ID", "IBLOCK_ID", "NAME", "DATE_ACTIVE_FROM","PROPERTY_*");    $arFilter = Array("IBLOCK_ID"=> $arItem['PROPERTIES']['SCHOOL']['LINK_IBLOCK_ID'], "ACTIVE_DATE"=>"Y", "ACTIVE"=>"Y", 'ID' => $arItem['PROPERTIES']['SCHOOL']['VALUE']);    $res = CIBlockElement::GetList(Array(), $arFilter, false, false, $arSelect);    while($ob = $res->GetNextElement()){        $arFields = $ob->GetFields();        $arProps = $ob->GetProperties();        $arResult['ITEMS'][$key]['PROPERTIES']['SCHOOL']['ITEM'] = ['FIELDS' => $arFields, 'PROPS' => $arProps];    }}