<?php    use \Wptt\Main as Main;    foreach ($arResult['ITEMS'] as $key => $arItem) {    $arSelect = Array("ID", "IBLOCK_ID", "NAME", "DATE_ACTIVE_FROM", 'PREVIEW_PICTURE', "PROPERTY_*");    $arFilter = Array("IBLOCK_ID"=> $arItem['PROPERTIES']['SCHOOL']['LINK_IBLOCK_ID'], "ACTIVE_DATE"=>"Y", "ACTIVE"=>"Y", 'ID' => $arItem['PROPERTIES']['SCHOOL']['VALUE']);    $res = CIBlockElement::GetList(Array(), $arFilter, false, false, $arSelect);    while($ob = $res->GetNextElement()){        $arFields = $ob->GetFields();        $arProps = $ob->GetProperties();        $arFields['PREVIEW_PICTURE'] = CFile::GetPath($arFields['PREVIEW_PICTURE']);        $arResult['ITEMS'][$key]['PROPERTIES']['SCHOOL']['ITEM'] = ['FIELDS' => $arFields, 'PROPS' => $arProps];    }    if ($arItem['IBLOCK_SECTION_ID'] === '5') {        $arResult['ITEMS'][$key]['STUDYING_PERIOD'] = $arItem['PROPERTIES']['STUDYING_PERIOD']['VALUE'] . ' ч';    } else {        $arResult['ITEMS'][$key]['STUDYING_PERIOD'] = 'от ';        if ($arItem['PROPERTIES']['STUDYING_PERIOD_YEAR']['VALUE']) {            $arResult['ITEMS'][$key]['STUDYING_PERIOD'] .= $arItem['PROPERTIES']['STUDYING_PERIOD_YEAR']['VALUE'] . ' ' .  Main::num2word($arItem['PROPERTIES']['STUDYING_PERIOD_YEAR']['VALUE'], ['года','лет','лет']) . ' ';        }        if ($arItem['PROPERTIES']['STUDYING_PERIOD_MONTH']['VALUE']) {            $arResult['ITEMS'][$key]['STUDYING_PERIOD'] .= $arItem['PROPERTIES']['STUDYING_PERIOD_MONTH']['VALUE'] . ' ' . 'мес.';        }    }}