<?php foreach ($arResult['ITEMS'] as $arItem){    $sectionsID[$arItem['IBLOCK_SECTION_ID']] = $arItem['IBLOCK_SECTION_ID'];}if (!empty($sectionsID)) {    $arFilter = Array('IBLOCK_ID'=>$arParams['IBLOCK_ID'], 'GLOBAL_ACTIVE'=>'Y', 'ID'=>$sectionsID);    $arSelect = ['ID', 'IBLOCK_ID' , 'NAME' , 'CODE' , 'UF_*'];    $db_list = CIBlockSection::GetList(Array('ID'=>'ASC'), $arFilter, false, $arSelect);    while($ar_result = $db_list->GetNext())    {        $arResult['SECTIONS'][$ar_result['ID']] = $ar_result;    }}